CREATE SCHEMA IF NOT EXISTS tas_demo;
CREATE TABLE IF NOT EXISTS tas_demo.tenant (
  tenant_id UUID PRIMARY KEY,
  tenant_name TEXT,
  onboarded_date_time_utc TIMESTAMP,
  tenant_code TEXT,
  is_active BOOLEAN
);
CREATE TABLE IF NOT EXISTS tas_demo.location (
  location_id UUID PRIMARY KEY,
  location_name TEXT,
  tenant_id UUID,
  is_active BOOLEAN
);
CREATE TABLE IF NOT EXISTS tas_demo.colleague_details (
  id UUID PRIMARY KEY,
  colleague_uuid UUID,
  colleague_payload TEXT,
  tenant_id UUID,
  location_id UUID
);
CREATE TABLE IF NOT EXISTS tas_demo.planned_shift (
  planned_shift_id UUID PRIMARY KEY,
  colleague_uuid UUID,
  start_date_time_utc TIMESTAMP,
  end_date_time_utc TIMESTAMP,
  shift_payload TEXT,
  created_time_stamp_utc TIMESTAMP,
  updated_time_stamp_utc TIMESTAMP,
  tenant_id UUID,
  snapshot_version TEXT
);
CREATE TABLE IF NOT EXISTS tas_demo.exception (
  exception_id UUID PRIMARY KEY,
  exception_type TEXT,
  location_uuid UUID,
  colleague_uuid UUID,
  exception_date_utc TIMESTAMP,
  tenant_id UUID,
  exception_duration INT,
  created_timestamp_utc TIMESTAMP,
  updated_timestamp_utc TIMESTAMP
);
CREATE TABLE IF NOT EXISTS tas_demo.exception_audit (
  audit_exception_id UUID PRIMARY KEY,
  exception_id UUID,
  exceptionAction TEXT,
  created_date_time_utc TIMESTAMP,
  manager_uuid UUID,
  payload TEXT
);
CREATE TABLE IF NOT EXISTS tas_demo.exception_detail (
  exception_summary_id UUID PRIMARY KEY,
  exception_id UUID,
  tenant_id UUID,
  start_date_time_utc TIMESTAMP,
  end_date_time_utc TIMESTAMP,
  duration_mins INT,
  is_balanced BOOLEAN,
  leaver_date TIMESTAMP,
  status TEXT
);
